import { Component, OnInit } from '@angular/core'
import { WeatherService } from './weather.service'
import { weatherConditions } from './weather_conditions'
import { CurrentWeather } from './types/Weather'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'mountain-hikes'
  // temp = undefined
  // weatherText = ''
  // weatherCode = undefined
  // photo = 0
  // date: Date = new Date();
  currentWeather: CurrentWeather = {
    temp: 0,
    text: '',
    code: 0,
    photo: 0,
    day: 0,
    month: 0,
    year: 0
  }

  constructor (public weatherService: WeatherService) {}

  ngOnInit () {
    this.weatherService.getCurrentWeather().subscribe(weather => {
      // this.temp = weather.current.temp_c
      // this.weatherText = weather.current.condition.text
      // this.weatherCode = weather.current.condition.code
      // const test = weatherConditions.find(obj => obj.code === this.weatherCode)
      // this.photo = test?.icon || 0; 
      // const currentDate = weather.forecast.forecastday[0].date;
      // weather.forecast.forecastday.forEach((element:any) => {
      //   console.log(element);
      // });
      // this.date = new Date(currentDate)
      // const day = this.date.getDate();    
      // const month = this.date.getMonth();    
      // const year = this.date.getFullYear()

      this.currentWeather.temp = weather.current.temp_c;
      this.currentWeather.text = weather.current.condition.text;
      this.currentWeather.code = weather.current.condition.code;
      const matchedCondition = weatherConditions.find(obj => obj.code === this.currentWeather.code);
      this.currentWeather.photo = matchedCondition?.icon || 0;
    })
  }
}
