import { Component, Input } from '@angular/core';
import { Post } from '../types/Post';

@Component({
  selector: 'app-hike',
  templateUrl: './hike.component.html',
  styleUrls: ['./hike.component.css']
})
export class HikeComponent {
  @Input() post: Post = {
    name: '',
    mountain: '',
    country: '',
    duration: '',
    description: '',
    photos: [],
    _id: ''
  };
}
<article>
    <div class="image-wrapper">
        <img src={{post.photos[0]}} alt="mountain peak" />
    </div>

    <div class="content-container">
        <h2>{{post.name}}</h2>
        <h3>{{post.mountain}} - {{post.country}}</h3>
        <p class="duration">Duration: {{post.duration}}</p>
        <div class="divider"></div>
        <p class="post-author">Posted by: tanya@gmail.com</p>
    </div>
</article>
