import { Component } from '@angular/core';
declare let gapi: any;
declare let google: any;


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'mountain-hikes';

  clientId = '241063891583-a3hga6p20lp1msqgnojjjuh407iu1i60.apps.googleusercontent.com';
  apiKey = 'AIzaSyCQIUZsGsnNJGXdPnqlUkf8FWox0YEvZ-Y';
  discoveryDoc = 'https://www.googleapis.com/discovery/v1/apis/drive/v3/rest';
  scopes = 'https://www.googleapis.com/auth/drive.metadata.readonly https://www.googleapis.com/auth/drive';
  tokenClient?: any;
  gapiInited = false;
  gisInited = false;

  initializeGapiClient() {
    gapi.client.init({
      apiKey: 'AIzaSyCQIUZsGsnNJGXdPnqlUkf8FWox0YEvZ-Y',
      discoveryDocs: 'https://www.googleapis.com/discovery/v1/apis/drive/v3/rest',
    }).then(() => {
      this.gapiInited = true;
    });    
  }

  gapiLoaded() {
    gapi.load('client', this.initializeGapiClient);
  }

  gisLoaded() {
    this.tokenClient = google.accounts.oauth2.initTokenClient({
      client_id: this.clientId,
      scope: this.scopes,
      callback: () => {},
    });
    this.gisInited = true;    
  }

  loadGoogleDrive() {
    this.gapiLoaded();
    this.gisLoaded();
  }

  listFiles() {
    let response;
    try {
      response = gapi.client.drive.files.list({
        'pageSize': 10,
        'fields': 'files(id, name)',
      }).then(() => {console.log('list resposne')});
    } catch (err: any) {
      console.log(err.message);
      return;
    }
    const files = response.result.files;
    if (!files || files.length == 0) {
      console.log('No files found.');
      return;
    }    
    const output = files.reduce((str: any, file: any) => `${str}${file.name} (${file.id})\n`,'Files:\n');
    console.log(output);
  }

  handleAuthClick() {
    this.tokenClient.callback = (resp: any) => {
      if (resp.error !== undefined) {
        throw (resp);
      }      
      this.listFiles();
    };

    if (gapi.client.getToken() === null) {      
      this.tokenClient.requestAccessToken({prompt: 'consent'});
    } else {      
      this.tokenClient.requestAccessToken({prompt: ''});
    }
  }

  handleSignoutClick() {
    const token = gapi.client.getToken();
    if (token !== null) {
      google.accounts.oauth2.revoke(token.access_token);
      gapi.client.setToken('');      
    }
  }
}
