import { Component, OnInit } from '@angular/core';
import { WeatherService } from './weather.service';
import { weatherConditions } from './weather_conditions';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'mountain-hikes';
  temp = undefined;
  weatherText = '';
  weatherCode = undefined;
  photo = 0;
  currentDate = '';

  constructor(public weatherService: WeatherService) {}

  ngOnInit() {
    this.weatherService.getCurrentWeather()
      .subscribe(weather => {
        this.temp = weather.current.temp_c;
        this.weatherText = weather.current.condition.text;
        this.weatherCode = weather.current.condition.code;
        const test = weatherConditions.find(obj => obj.code === this.weatherCode);
        this.photo = test?.icon || 0;
        this.currentDate = weather.forecast.forecastday[0].date;
        const date = new Date(this.currentDate);
        console.log(this.temp, this.weatherText, this.weatherCode, this.currentDate, date);
      })
  }
}
