import { Component, Input } from '@angular/core';
import { CurrentWeather, ForecastWeather } from '../types/Weather';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
  @Input() currentWeather: CurrentWeather = {
    temp: 0,
    text: '',
    code: 0,
    photo: 0,
    date: ''
  };

  @Input() forecastWeather: ForecastWeather[] = [];
}
<div class="weather">
  <h2>Weather in the region of peak Canin</h2>
  <div>
    <div class="current-weather">
      <p>The Weather rigth now</p>
      <img src="../assets/{{ currentWeather.photo }}.png" alt="weather" />
      <p>{{ currentWeather.text }} - {{ currentWeather.temp }}&#176;C</p>
    </div>

    <div class="forecast-weather">

    </div>
  </div>
</div>
