import { Component } from '@angular/core';

@Component({
  selector: 'app-hike',
  templateUrl: './hike.component.html',
  styleUrls: ['./hike.component.css']
})
export class HikeComponent {

}
<article>
    <div class="image-wrapper">
        <img src="https://huttohuthikingdolomites.com/wp-content/uploads/Commanding-Monte-Civetta.jpg" alt="mountain peak">
    </div>

    <div class="content-container">
        <h2>Peak Canin</h2>
        <h3>Julian Alps - Slovenia</h3>
        <p>Duration: 3 hours</p>
        <div class="divider"></div>
        <p class="post-author">Posted by: tanya@gmail.com</p>
    </div>
</article>
